import React, { useEffect, useState } from "react";
import "../App.css";
import Parse from "parse";
import { Link } from "react-router-dom";

//COMPONENTS
import LiveChat from "./LiveChat";
import ChatList from "../components/DisplayUsers/DisplayUsers";
import ChatRoom from "../pages/ChatRoom/ChatRoom";

export default function ChatSetup(props) {
  const [senderUserName, setSenderUserName] = useState("");
  const [senderUserId, setSenderUserId] = useState(null);
  const [receiverUserName, setReceiverUserName] = useState("");
  const [receiverUserId, setReceiverUserId] = useState(null);
  const [chatRoomId, setChatRoomId] = useState(null);

  useEffect(() => {
    // setSenderUserName(props.currentUser.get("username"));
    setReceiverUserName(props.selectedUser);
  }, []);

  async function startLiveChat() {
    console.log("Current user name: ", senderUserName);
    const usersArray = [senderUserName, receiverUserName];
    console.log("users array", usersArray);
    console.log("Starting live chat");

    // check if both *UserName variables hold any values
    if (senderUserName === null || receiverUserName === null) {
      alert("Please inform both sender and receiver user names!");
      return false;
    }

    let chatRoomObject = null;
    try {
      const chatRoomParseQuery = new Parse.Query("ChatRoom");
      // HOW TO MAKE QUERY BASED ON ARRAY ???
      chatRoomParseQuery.containsAll("users", usersArray); // existing chatroom id: "XpdGCZsn2E" ?
      const chatRoomParseQueryResult = await chatRoomParseQuery.first();
      if (
        chatRoomParseQueryResult !== undefined &&
        chatRoomParseQueryResult !== null
      ) {
        console.log("Chat Room ID:", chatRoomParseQueryResult.id);
        chatRoomObject = chatRoomParseQueryResult;
      } else {
        alert("CREATING NEW CHAT ROOM");
        chatRoomObject = new Parse.Object("ChatRoom");
        chatRoomObject.set("users", usersArray);
        chatRoomObject.save();
      }
    } catch (error) {
      console.log("ChatRoom Error", error);
      return false;
    }

    /*     let senderUserObject = null;
    try {
      const senderParseQuery = new Parse.Query("User");
      senderParseQuery.equalTo("username", senderUserName);
      const senderParseQueryResult = await senderParseQuery.first();
      if (
        senderParseQueryResult !== undefined &&
        senderParseQueryResult !== null
      ) {
        senderUserObject = senderParseQueryResult;
      } else {
        alert("Sender does not exist!");
      }
    } catch (error) {
      alert(error);
      return false;
    }

    let receiverUserObject = null;
    try {
      const receiverParseQuery = new Parse.Query("User");
      receiverParseQuery.equalTo("username", receiverUserName);
      const receiverParseQueryResult = await receiverParseQuery.first();
      if (
        receiverParseQueryResult !== undefined &&
        receiverParseQueryResult !== null
      ) {
        receiverUserObject = receiverParseQueryResult;
        console.log("Receiver User ID: ", receiverUserObject.id);
        props.setSelectedUser(senderUserObject);
      } else {
        alert("Receiver does not exist!");
      }
    } catch (error) {
      alert("Caught this error: ", error);
      return false;
    } */

    // set user object ids, so live chat component is instantiated
    setSenderUserId(props.currentUser.id);
    setReceiverUserId(props.selectedUser.id);
    setChatRoomId(chatRoomObject.id);
    console.log("RECEIVER USER NAME: ", receiverUserName);
    console.log("RECEIVER USER ID: ", receiverUserId);
    return true;
  }

  startLiveChat();

  return (
    senderUserId !== null &&
    senderUserId !== undefined &&
    receiverUserId !== null &&
    receiverUserId !== undefined && (
      <div>
        <h1>Live chat started! </h1>
        <LiveChat {...props} />
      </div>
    )
  );
}
