import React, { useState, useEffect } from "react";
import Parse from "parse";
import ChatSetup from "../../api/ChatSetup";

export default function ChatList(props) {
  const [localUsers, setLocalUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState("");

  useEffect(() => {
    setCurrentUser(props.parseUserCurrent.get("username"));
    queryAllUsers().then((result) => setLocalUsers(result));
  }, []);

  console.log("Current user: ", currentUser);

  async function queryAllUsers() {
    try {
      const queryUsers = new Parse.Query("User");
      queryUsers.notEqualTo("username", currentUser.get("username"));
      const allUserObjects = await queryUsers.find();
      const result = allUserObjects.map(
        (userobject) => (userobject = userobject.get("username"))
      );
      return result;
    } catch (error) {
      console.log(error);
    }
  }

  console.log("localUsers: ", localUsers);

  const listNames = localUsers.map(
    (name) =>
      (name = (
        <li>
          <ChatSetup
            key={name}
            receiverNameInput={name}
            senderNameInput={currentUser}
          />
        </li>
      ))
  );

  return <ul>{listNames}</ul>;
}
