import React, { useEffect, useState } from "react";
import "../App.css";
import Parse from "parse";
import { checkSenderAndReceiver, chatRoomSetup } from "./chat";

//COMPONENTS
import LiveChat from "./LiveChat";

export default function ChatSetup(props) {
  const [sender, setSender] = useState(Parse.User.current());
  const [receiver, setReceiver] = useState(props.selectedUser);
  const [chatRoom, setChatRoom] = useState("");

  const usersArray = [sender, props.receiver];

  useEffect(() => {
    if (receiver.id === undefined) {
      setReceiver(props.selectedUser);
    }
    console.log("Chatroom receiver ", receiver);
  }, []);

  useEffect(() => {
    checkSenderAndReceiver(sender, props.receiver);

    //
    chatRoomSetup(usersArray).then((result) => {
      console.log(result);
      setChatRoom(result);
    });
    //
  }, []);

  // console.log("Current user:", sender);
  // console.log("Selected user:", receiver);
  // console.log("Chatroom Setup:", chatRoom);

  return (
    <div>
      {chatRoom === null && <h1> chat setup ...</h1>}

      {chatRoom !== undefined &&
        chatRoom !== null &&
        sender.id !== undefined &&
        props.receiver.id !== undefined && (
          <div>
            <h1>Messages </h1>
            <LiveChat
              chatRoom={chatRoom}
              receiver={props.receiver}
              sender={sender}
            />
          </div>
        )}
    </div>
  );
}
