import React, { useState } from "react";
import "../App.css";
import Parse from "parse";
import LiveChat from "./LiveChat";
import { initializeParse } from "@parse/react";

// Note the live query URL instead of the regular server url
const PARSE_APPLICATION_ID = "XCuriaWlKUTt14yvNJVJ77kjQyC8yKFIOFqiVFck";
// const PARSE_SERVER_URL = "https://parseapi.back4app.com/";
const PARSE_LIVE_QUERY_URL = "https://tripster.b4a.io/";
const PARSE_JAVASCRIPT_KEY = "JxCHF3R5JB8VH3r8UXI9LCCEI14N3IdNIvrRhgIL";

// Initialize parse using @parse/react instead of regular parse JS SDK
initializeParse(
  PARSE_LIVE_QUERY_URL,
  PARSE_APPLICATION_ID,
  PARSE_JAVASCRIPT_KEY
);

// Parse.initialize(
//   "XCuriaWlKUTt14yvNJVJ77kjQyC8yKFIOFqiVFck",
//   "JxCHF3R5JB8VH3r8UXI9LCCEI14N3IdNIvrRhgIL"
// ); //PASTE HERE YOUR Back4App APPLICATION ID AND YOUR JavaScript KEY
// Parse.serverURL = "https://parseapi.back4app.com/";
// // Parse.serverURL = "tripster.b4a.io";

export default function ChatSetup() {
  const [senderUserName, setSenderUserName] = useState("aaa");
  const [senderUserId, setSenderUserId] = useState(null);
  const [receiverUserName, setReceiverUserName] = useState("bbb");
  const [receiverUserId, setReceiverUserId] = useState(null);

  const queryCurrentUser = new Parse.Query("User");
  currentUserResult = queryCurrentUser.equalTo();

  (async () => {
    try {
      const currentUser = Parse.User.current();
      console.log("Current logged in user", currentUser);
    } catch (error) {
      console.error("Error while logging in user", error);
    }
  })();

  setSenderUserName();

  async function startLiveChat() {
    console.log("Starting live chat");

    // check if both *UserName variables hold any values
    if (senderUserName === null || receiverUserName === null) {
      alert("Please inform both sender and receiver user names!");
      return false;
    }

    let senderUserObject = null;
    try {
      const senderParseQuery = new Parse.Query("User");
      senderParseQuery.equalTo("username", senderUserName);
      const senderParseQueryResult = await senderParseQuery.first();
      if (
        senderParseQueryResult !== undefined &&
        senderParseQueryResult !== null
      ) {
        console.log(senderParseQueryResult);
        senderUserObject = senderParseQueryResult;
      } else {
        alert("Sender does not exist!");
      }
    } catch (error) {
      alert(error);
      return false;
    }

    let receiverUserObject = null;
    try {
      const receiverParseQuery = new Parse.Query("User");
      receiverParseQuery.equalTo("username", receiverUserName);
      const receiverParseQueryResult = await receiverParseQuery.first();
      if (
        receiverParseQueryResult !== undefined &&
        receiverParseQueryResult !== null
      ) {
        receiverUserObject = receiverParseQueryResult;
      } else {
        alert("Receiver does not exist!");
      }
    } catch (error) {
      alert("Caught this error: ", error);
      return false;
    }

    // set user object ids, so live chat component is instantiated
    setSenderUserId(senderUserObject.id);
    setReceiverUserId(receiverUserObject.id);

    return true;
  }

  return (
    <div>
      <h1> CHAT SETUP PAGE</h1>
      {senderUserId === null && receiverUserId === null && (
        <button onClick={startLiveChat}>Start Live Chat</button>
      )}
      {senderUserId !== null && receiverUserId !== null && (
        <LiveChat
          senderUserName={senderUserName}
          senderUserId={senderUserId}
          receiverUserName={receiverUserName}
          receiverUserId={receiverUserId}
        />
      )}
    </div>
  );
}
