import React, { useEffect, useState } from "react";
import "../App.css";
import Parse from "parse";
import { useParseQuery } from "@parse/react";
import ChatSetup from "./ChatSetup";

export default function LiveChat(props) {
  // useEffect(() => {
  //   reload();
  // });

  console.log(
    props.senderUserId,
    props.receiverUserId,
    props.senderUserName,
    props.receiverUsername
  );

  const [messageInput, setMessageInput] = useState("");

  const parseQuery = new Parse.Query("Message");
  // get messsages that involve both nicknames
  parseQuery.containedIn("senderId", [
    props.senderUserId,
    props.receiverUserId,
  ]);
  parseQuery.containedIn("receiverId", [
    props.senderUserId,
    props.receiverUserid,
  ]);
  parseQuery.ascending("createdAt");
  parseQuery.includeAll();

  // console.log(parseQuery);

  const { isLive, isLoading, isSyncing, results, count, error, reload } =
    useParseQuery(parseQuery, {
      enableLocalDatastore: true,
      enableLiveQuery: true,
    });

  async function sendMessage() {
    try {
      const messageText = messageInput;

      // get sender and receiver username parse objects
      // const senderNameObjectQuery = new Parse.Query("User");
      // senderNameObjectQuery.equalTo("username", props.senderUserName);
      // let senderNameObject = await senderNameObjectQuery.first();

      // const receiverNameObjectQuery = new Parse.Query("User");
      // receiverNameObjectQuery.equalTo("username", props.receiverUserName);
      // let receiverNameObject = await receiverNameObjectQuery.first();

      // get sender and receiver ID parse objects
      const senderIdObjectQuery = new Parse.Query("User");
      senderIdObjectQuery.equalTo("objectId", props.senderUserId);
      let senderIdObject = await senderIdObjectQuery.first();

      const receiverIdObjectQuery = new Parse.Query("User");
      receiverIdObjectQuery.equalTo("objectId", props.receiverUserId);
      let receiverIdObject = await receiverIdObjectQuery.first();

      // get chatRoom ID parse object
      const chatRoomObjectQuery = new Parse.Query("ChatRoom");
      chatRoomObjectQuery.equalTo("objectId", props.chatRoomId);
      let chatRoomObject = await chatRoomObjectQuery.first();

      // create new message object and save it
      let Message = new Parse.Object("Message");
      Message.set("text", messageText);
      Message.set("senderId", senderIdObject);
      Message.set("receiverId", receiverIdObject);
      // Message.set("sender", senderNameObject);
      // Message.set("receiver", receiverNameObject);
      Message.set("chatRoomId", chatRoomObject); // object from setup!
      Message.save();
      alert("New message saved: " + messageText);

      setMessageInput("");
    } catch (error) {
      alert(error);
    }
  }

  // Helper to format createdAt value on Message
  const formatDateToTime = (date) => {
    return `${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`;
  };

  return (
    <div>
      {/* {results && (
        <div className="messages">
          {results
            .sort((a, b) => a.get("createdAt") > b.get("createdAt"))
            .map((result) => (
              <div
                key={result.id}
                className={
                  result.get("senderId").id === props.senderUserId
                    ? "message_sent"
                    : "message_received"
                }
              >
                <p className="message_bubble">{result.get("text")}</p>
                <p className="message_time">
                  {formatDateToTime(result.get("createdAt"))}
                </p>
                <p className="message_name">
                  {result.get("senderId").get("name")}
                </p>
              </div>
            ))}
        </div>
      )} */}

      <h2>
        {`${props.senderUserName} sending, ${props.receiverUserName} receiving!`}
      </h2>

      <div className="new_message">
        <h2 className="new_message_title">New message</h2>
        <input
          value={messageInput}
          onChange={(event) => setMessageInput(event.target.value)}
          placeholder={"Your message..."}
          size="large"
        />
        <button
          type="primary"
          className="form_button"
          color={"#208AEC"}
          size={"large"}
          onClick={sendMessage}
        >
          Send message
        </button>
      </div>
      <div className="message-list-status">
        {isLoading && <p>{"Loading…"}</p>}
        {isSyncing && <p>{"Syncing…"}</p>}
        {isLive ? <p>{"Status: Live"}</p> : <p>{"Status: Offline"}</p>}
        {error && <p>{error.message}</p>}
        {count && <p>{`Count: ${count}`}</p>}
      </div>
    </div>
  );
}
