import React, { useEffect, useState } from "react";
import "./../../App.css";
import Parse from "parse";
import { checkSenderAndReceiver, chatRoomSetup } from "../../api/chat";

//COMPONENTS
import LiveChat from "../LiveChat/LiveChat";

export default function ChatSetup(props) {
  const [sender, setSender] = useState(Parse.User.current());
  const [receiver, setReceiver] = useState(props.selectedUser); // DELETE
  const [chatRoom, setChatRoom] = useState("");

  const usersArray = props.chatUsers;

  // useEffect(() => {
  //   if (receiver.id === undefined) {
  //     setReceiver(props.selectedUser);
  //   }
  //   console.log("Chatroom receiver ", receiver);
  // }, []);

  useEffect(() => {
    // checkSenderAndReceiver(sender, receiver);

    //
    chatRoomSetup(usersArray).then((result) => {
      console.log(result);
      setChatRoom(result);
    });
    //
  }, []);

  // console.log("Current user:", sender);
  // console.log("Selected user:", receiver);
  // console.log("Chatroom Setup:", chatRoom);

  return (
    <div>
      {chatRoom === null && <h1> chat setup ...</h1>}

      {chatRoom !== undefined &&
        chatRoom !== null &&
        sender.id !== undefined &&
        receiver.id !== undefined && (
          <div>
            <h1>Messages </h1>
            <LiveChat chatRoom={chatRoom} receiver={receiver} sender={sender} />
          </div>
        )}
    </div>
  );
}
