import React from "react";
import { useState } from "react";
import "./../../App.css";
import Button from "../Button/Button";
import { doUserLogIn, queryBookingId } from "../../api/user";
import "./LogInForm.css";
import Parse from "parse";
import { initializeParse } from "@parse/react";
import { logInUser } from "../../api/user";
import { Link } from "react-router-dom";

// Initialize Parse
Parse.initialize(
  "XCuriaWlKUTt14yvNJVJ77kjQyC8yKFIOFqiVFck",
  "JxCHF3R5JB8VH3r8UXI9LCCEI14N3IdNIvrRhgIL"
); //PASTE HERE YOUR Back4App APPLICATION ID AND YOUR JavaScript KEY
Parse.serverURL = "https://parseapi.back4app.com/";

export default function LogInForm() {
  const [bookingNumber, setBookingNumber] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [currentUser, setCurrentUser] = useState(null);
  // const [user, setUser] = useState("");

  // const getCurrentUser = async function () {
  //   //   const currentUser = await Parse.User.current();

  //   setCurrentUser(currentUser);
  //   return currentUser;
  // };

  function handleBookingNumberChange(event) {
    setBookingNumber(event.target.value);
  }

  function handleInputChange(event) {
    setUsername(event.target.value);
  }

  function handlePasswordChange(event) {
    setPassword(event.target.value);
  }

  function handleFormSubmit(event) {
    event.preventDefault();
    logInUser(username, password);
    setCurrentUser(Parse.User.current());

    /// move that part ^
  }

  return (
    <div className="log-in-form">
      <form onSubmit={handleFormSubmit}>
        {/*Booking number*/}
        <div className="form-item">
          <label htmlFor="bookingnumber">Booking number</label>
          <input
            type="text"
            className="form-control"
            id="bookingnumber"
            value={bookingNumber}
            onChange={handleBookingNumberChange}
          />
        </div>

        {/*Username*/}
        <div className="form-item">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            className="form-control"
            id="username"
            value={username}
            onChange={handleInputChange}
          />
        </div>

        {/*Password*/}
        <div className="form-item">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>

        <div className="log-in-button">
          <Link to={currentUser ? "/login" : "/discover"}>
            <Button onClick={handleFormSubmit} text="Log in" type="login" />
          </Link>
        </div>
      </form>
    </div>
  );
}
