import React, { useState, useEffect } from "react";
import Parse from "parse";
import UserLink from "../UserLink/UserLink";
import ChatSetup from "../ChatSetup/ChatSetup";

export default function DisplayUsers(props) {
  const [localUsers, setLocalUsers] = useState([]);
  const [activeChats, setActiveChats] = useState([]);

  useEffect(() => {
    // In Discover page:
    if (props.displayMode === "discover") {
      queryAllUsers().then((result) => {
        if (localUsers.length === 0) {
          setLocalUsers(result);
        } else {
          console.log("localUsers is set");
        }
      });
    }
    // In ActiveChats page:
    else if (props.displayMode === "active") {
      queryActiveChats().then((result) => {
        if (activeChats.length === 0) {
          setActiveChats(result);
        } else {
          console.log("active chats set");
        }
      });
    }
  }, []);

  //// IMPORT THIS FROM API
  async function queryActiveChats() {
    try {
      const currentUser = Parse.User.current();
      console.log("querying chats with", currentUser.get("username"));
      const queryChats = new Parse.Query("ChatRoom");
      queryChats.containsAll("users", [currentUser]);
      const allActiveChats = await queryChats.find();
      if (allActiveChats.length === 0) {
        return false;
      }

      console.log("chats list", allActiveChats);
      allActiveChats.forEach((chat) =>
        console.log(
          "chat users",
          chat.get("users").forEach((user) => console.log(user.get("username")))
        )
      );

      return allActiveChats;
    } catch (error) {
      console.log(error);
    }
  }

  //// IMPORT THIS FROM API
  async function queryAllUsers() {
    try {
      const currentUser = Parse.User.current();
      const queryUsers = new Parse.Query("User");
      queryUsers.notEqualTo("objectId", currentUser.id);
      const allUserObjects = await queryUsers.find();

      return allUserObjects;
    } catch (error) {
      console.log(error);
    }
  }
  ////

  return (
    <div>
      {localUsers === false && (
        <h3 className="body">
          You have no active chats. Find someone to chat with on the 'Discover'
          page.
        </h3>
      )}

      {localUsers && (
        <ul>
          {localUsers.map((user) => (
            <UserLink
              key={user.id}
              onClick={() => props.setSelectedUser(user)}
              to="/chatroom"
              username={user.get("username")}
              nationality={user.get("nationality")}
              status={user.get("status")}
              photo={user.get("photo").url()}
            />
          ))}
        </ul>
      )}

      {activeChats && (
        <ul>
          {activeChats.map((chat) => {
            return (
              // <h1>{chat.get("users")[0]}</h1>
              ///

              <UserLink
                key={chat.id}
                onClick={() => props.setSelectedUser(chat.get("users"))}
                to="/chatroom"
                // username={chat.get("users")[1].get("username")}
              />
            );
          })}
        </ul>
      )}
    </div>

    //
  );
}
