import "./../../App.css";
import Parse from "parse";
import { chatRoomSetup, checkUsersArray } from "../../api/chat";

//COMPONENTS
import LiveChat from "../LiveChat/LiveChat";

export default function ChatSetup(props) {
  const [sender, setSender] = useState(Parse.User.current());
  const [chatRoom, setChatRoom] = useState("");

  const usersArray = props.chatUsers;

  useEffect(() => {
    checkUsersArray(usersArray);

    //
    chatRoomSetup(usersArray).then((result) => {
      console.log(result);
      setChatRoom(result);
    });
    //
  }, []);

  // console.log("Current user:", sender);
  // console.log("Selected user:", receiver);
  // console.log("Chatroom Setup:", chatRoom);

  return (
    <div>
      {chatRoom === null && <h1> chat setup ...</h1>}

      {chatRoom !== undefined &&
        chatRoom !== null &&
        sender.id !== undefined && (
          /* receiver.id !== undefined && */ <div>
            <h1>Messages </h1>
            <LiveChat chatRoom={chatRoom} sender={sender} receiver={receiver} />
          </div>
        )}
    </div>
  );
}
