import React, { useState } from "react";
import "../App.css";
import Parse from "parse";
import { checkSenderAndReceiver, chatRoomSetup } from "./chat";

//COMPONENTS
import LiveChat from "./LiveChat";

export default function ChatSetup(props) {
  const sender = Parse.User.current();
  const receiver = props.selectedUser;
  const usersArray = [sender, receiver];
  const [chatRoom, setChatRoom] = useState(null);

  checkSenderAndReceiver(sender, receiver);

  if (chatRoom === null || chatRoom === undefined) {
    chatRoomSetup(usersArray);
  }
  console.log("Current user:", Parse.User.current());
  console.log("Selected user:", props.selectedUser);

  if (chatRoom === null || chatRoom === undefined) {
    setChatRoom(chatRoomSetup(usersArray));
  }

  return (
    <div>
      {chatRoom === undefined ||
        (chatRoom === null && <h1> chat setup ...</h1>)}

      {chatRoom !== undefined && chatRoom !== null && (
        <div>
          <h1>Messages </h1>
          <LiveChat chatRoom={chatRoom} sender={sender} receiver={receiver} />
        </div>
      )}
    </div>
  );
}
