// DISPLAY USERS

import React, { useState, useEffect } from "react";
import Parse from "parse";
import { Link } from "react-router-dom";
import ChatSetup from "../../api/ChatSetup";

export default function DisplayUsers(props) {
  const [localUsers, setLocalUsers] = useState([]);

  useEffect(() => {
    queryAllUsers().then((result) => {
      // console.log("useEffect:", result);
      if (localUsers.length === 0) {
        setLocalUsers(result);
      }
    });
  }, []);

  async function queryAllUsers() {
    try {
      const currentUser = Parse.User.current();
      const queryUsers = new Parse.Query("User");
      queryUsers.notEqualTo("objectId", currentUser.id);
      const allUserObjects = await queryUsers.find();
      // console.log("Current user:", Parse.User.current());
      return allUserObjects;
    } catch (error) {
      console.log(error);
    }
  }
  console.log("localUsers: ", localUsers);

  const localUsersDisplay = localUsers.map(
    (user) =>
      (user = (
        <li key={user.id}>
          <Link to="/chatroom" onClick={props.setSelectedUser(user)}>
            {user.get("username")}
          </Link>
        </li>
      ))
  );

  return <ul>{localUsersDisplay}</ul>;
}
