import React from "react";
import "./UserLink.css";
import "./../../App.css";
import Parse from "parse";
import { Link } from "react-router-dom";

export default function ChatLink(props) {
  //

  if (props.chat === undefined) {
    return;
  }
  const chatObject = props.chat;
  const usersInChat = chatObject.get("users");
  console.log("fetching users", chatObject.get("users"));

  let usernames = [];

  usersInChat.forEach((user) => {
    if (user.id === Parse.User.current().id) {
      return;
    } else {
      usernames.push(user.get("username"));
    }
  });

  const usernamesToDisplay = usernames[1]
    ? `${usernames[0]} and others`
    : usernames[0];

  // console.log("other users in chat", usernames);

  return (
    <div>
      <Link to={props.to} onClick={props.onClick}>
        <img src={props.photo} className="userlink-image" />
        <div className="user-link-username">
          <div className="body">{usernamesToDisplay}</div>
        </div>
        <div className="userlink-description">
          <div className="header"> active chat </div>
          <div className="last-message"> last message </div>
        </div>
      </Link>
    </div>
  );
}
